/**
 * @param {string} word
 * @return {number}
 */
var longestBeautifulSubstring = function(word) {
  let findMap = {
    b: '0a', // begin
    a: 'ae',
    e: 'ei',
    i: 'io',
    o: 'ou',
    u: 'u',
  }
  let curFind = 'b'
  let temp = 0
  word += 'a'
  for (let begin = 0, end = 0; end < word.length; ) {
    // debugger
    let index = findMap[curFind].indexOf(word[end])
    if (index === -1) {
      if (curFind === 'u') {
        temp = Math.max(temp, end - begin)
      }
      if (word[end] !== 'a') {
        end++
      }
      curFind = 'b'
      begin = end
    } else {
      if (index === 1) {
        curFind = findMap[curFind][1]
      }
      end++
    }
  }
  return temp
}
